import './App.css';
import {useState} from 'react';

function App() {
    const [todoList,setTodoList] = useState([]);
    const [newTask,setNewTask] = useState("");

    const handleChange = (event) =>{
        setNewTask(event.target.value);
    }

    // In order to add new values to the todoList array, we need to create another array
    // which receives all the values from the todoList + the new value we want to add
    // this new array is indicated with the [] surrounding both variables
    // the spread operator (...) is used to copy all the values from an array
    const addTask = () =>{
        setTodoList([...todoList,newTask]);
    }

    const deleteTask = (taskName) =>{
        {/*const newTodoList = todoList.filter((task) =>{
            if(task === taskName){
                return false;
            }
            else{
                return true;
            }
        })*/}
        {/*The code above is the same as the code inside the setTodoList function
           it uses the filter function to loop through every task, and if the task
           is the same as the taskName, then it returns false(it doesn't return it,
           so it is removed from the array) and if it's not then it returns true*/}
        setTodoList(todoList.filter((task) => task !== taskName));
    }
  return (
    <div className="App">

      <div className="addTask">
      <input onChange={handleChange}/>
      <button onClick={addTask}>Add Task</button>
      </div>

      <div className="list">
      {/*Uses the map method to show everything inside the array, passing the task value as
         a parameter and displaying it*/}
      {todoList.map((task) => {
          return <div>
                    <h1>{task}</h1>
                    {/*Whenever there's a parameter beign sent to the function on a button'
                       onClick, we need to make a arrow function which calls the deleteTask
                       function*/}
                    <button onClick={() => deleteTask(task)}> X </button>
                 </div>
      })}
      </div>

    </div>
  );
}

export default App;
