import './App.css';
import {useState} from 'react';

function App() {
    const [todoList,setTodoList] = useState([]);
    const [newTask,setNewTask] = useState("");

    const handleChange = (event) =>{
        setNewTask(event.target.value);
    }

    // Para adicionar novos valores na array todoList, precisamos criar outra array
    // a qual recebe todos os valores de todoList + o novo valor que queremos adicionar
    // o spread operator (...) serve para copiar todos os valores de uma array
    const addTask = () =>{
        const newTodoList = [...todoList,newTask];
        setTodoList(newTodoList);
    }
  return (
    <div className="App">

      <div className="addTask">
      <input onChange={handleChange}/>
      <button onClick={addTask}>Add Task</button>
      </div>

      <div className="list">
      {todoList.map((task) => {
          return <h1>{task}</h1>;
      })}
      </div>

    </div>
  );
}

export default App;
