import './App.css';
import {useState} from 'react';

let name = <h1>Adata</h1>;

function App() {
const [emJogo,setEmJogo] = useState(false);

    const iniciarPartida = () =>{
        setEmJogo(true);
    }

const [tem21,setTem21] = useState(false);
const somaTem21 = () =>{
    setTem21(true);
}

const [somaJogador,setSomaJogador] = useState(0);
const [somaDealer,setSomaDealer] = useState(0);

  return (
    <div className="App">
      {emJogo && console.log("em jogo")}
      {tem21 && console.log("tem o 21")}
      <button onClick={iniciarPartida}>Jogar</button>
      <button onClick={somaTem21}>21 instantaneo</button>
      <CartasJogador primeiraCarta={ randomCard() } segundaCarta={ randomCard() }/>
      <CartasDealer primeiraCartaDealer={ randomCard() } segundaCartaDealer={ randomCard() }/>
      <br/>
      <br/>
      {}
    </div>
  );

}

const CartasJogador = (props) => {
    return(
        <div>
        <p>Suas cartas:</p>
        <p>{props.primeiraCarta}</p>
        <p>{props.segundaCarta}</p>
        </div>
    );
};

const CartasDealer = (props) => {
    return(
        <div>
        <p>Cartas do Dealer:</p>
        <p>{props.primeiraCartaDealer}</p>
        <p>{props.segundaCartaDealer}</p>
        </div>
    );
};

function randomCard(){
    let random = Math.floor(Math.random() * 13) + 1
    if (random > 10){
        return 10
    }

    else if (random === 1){
        return 11
    }

    else {
        return random 
    }
}

export default App;
